(23095, 554, 'cs_rows_1_columns_b', '<img class=\"alignnone wp-image-549 size-full\" src=\"http://localhost/product-chain/wp-content/uploads/2018/07/Chewyvites-Kids-2018-30s-02.png\" alt=\"Chewyvites-Kids-2018-30\'s-01\" width=\"523\" height=\"520\" />'),
(23096, 554, '_cs_rows_1_columns_b', 'field_5b4f3a4ddbc29'),
(23097, 554, 'cs_rows_2_column_a', '<h2>Results</h2>\r\nThese important changes helped us expand the range in Superdrug and secure new distribution in Asda. Within the last 5 years we have increased sales more than sevenfold and are currently very close to securing additional listings in 2 other major retailers.'),
(23098, 554, '_cs_rows_2_column_a', 'field_5b4f391fdbc28'),
(23099, 554, 'cs_rows_2_columns_b', '<img class=\"alignnone wp-image-550 size-full\" src=\"http://localhost/product-chain/wp-content/uploads/2018/07/Chewyvites-Kids-2018-30s-03.png\" alt=\"Chewyvites-Kids-2018-30\'s-01\" width=\"521\" height=\"520\" />'),
